Addon.JavascriptInHead:
//
// --------------------
// -- server post
// server reads data from cp.request.body then deserializes to a request object of type that matches this data object
// on success, callback with responseData
// on fail, callback with error message
function serverPost(url, requestData, successCallBack, errorCallback) {
    console.log("serverPost, url [" + url + "], data [" + requestData + "]");
    $.ajax({
        type: "POST",
        url: url,
        data: JSON.stringify(requestData),
        contentType: "application/json; charset=utf-8",
        dataType: "json",
        success: function (response) {
            if (response.errors.length > 0) {
                errorCallback(response.errors[0]);
                return;
            }
            // -- update seat buttons
            successCallBack(response.data);
        },
        error: function (xhr) {
            if(xhr.responseText) {
                errorCallback(xhr.responseText);
                return;
            }
            errorCallback("Please try again. There was an unexpected error from " + url + ".");
        }
    });
}
//
// --------------------
//	-- child list methods
//
function saveSortable(listId) {
    let e, c, requestData = {};
    requestData.listId = listId;
    requestData.childList = "";
    e = document.getElementById(listId);
    for (i = 0; i < e.childNodes.length; i++) {
        c = e.childNodes[i];
        if (c.id) { requestData.childList += "," + c.id }
    }
  serverPost("savePageManagerChildListSort", requestData, function(){}, function(){});
}
$(function () {
    jQuery(".ccEditWrapper .ccChildList").sortable({
        items: "li.allowSort",
        stop: function (event, ui) {
            saveSortable(jQuery(this).attr("id"));
        },
        connectWith: ".ccChildList",
		receive: function(event,ui) {
            saveSortable(jQuery(this).attr("id"));
		},		
		placeholder: "ui-state-highlight"
    });
})
